import { pickProps } from '../utils/components';
import { props as stickyProps } from '../sticky/props';

function directionValidator(direction) {
  return ['horizontal', 'vertical'].includes(direction);
}

export var props = {
  active: {
    type: [String, Number],
    default: 0
  },
  layoutDirection: {
    type: String,
    default: 'horizontal',
    validator: directionValidator
  },
  itemDirection: {
    type: String,
    default: 'horizontal',
    validator: directionValidator
  },
  fixedBottom: {
    type: Boolean,
    default: false
  },
  activeColor: {
    type: String
  },
  inactiveColor: {
    type: String
  },
  disabledColor: {
    type: String
  },
  color: {
    type: String
  },
  indicatorColor: {
    type: String
  },
  indicatorSize: {
    type: [String, Number]
  },
  elevation: {
    type: Boolean,
    default: false
  },
  sticky: {
    type: Boolean,
    default: false
  },
  safeArea: {
    type: Boolean,
    default: false
  },
  offsetTop: pickProps(stickyProps, 'offsetTop'),
  onClick: {
    type: Function
  },
  onChange: {
    type: Function
  },
  'onUpdate:active': {
    type: Function
  }
};